function statisticsInit(){document.location.href.indexOf("statistics")<0||($(".view-log").on("click",function(){$.post("/admin/statistics/ajax",{action:"get-log"},function(t){bootbox.dialog({title:"Last 1000 Requests",message:'<textarea class="form-control" rows="30" style="font-size: 10px;">'+t+"</textarea>",className:"big-modal",buttons:{success:{label:"Ok",className:"btn-success",callback:function(){}}}})})}),$.post("/admin/statistics/ajax",{action:"get-stat"},function(t){var a=[],s=[],e=[];t=JSON.parse(t),$.each(t,function(t,o){a.push(o.period),s.push(o.uniques),e.push(o.total)});var o={labels:a,datasets:[{label:"Views",backgroundColor:"rgba(220,220,220,0.2)",borderColor:"rgba(220,220,220,1)",borderWidth:1,fill:!1,data:e},{label:"Unique visitors",backgroundColor:"rgba(151,187,205,0.2)",borderColor:"rgba(151,187,205,1)",borderWidth:1,fill:!1,data:s}]},i=document.getElementById("canvas").getContext("2d");window.myBar=new Chart(i,{type:"line",data:o,options:{responsive:!0,title:{display:!0,text:"Attendance in 10 days"}},scaleStartValue:0})}))}